;jQuery(document).ready(function($){let $providers=$(['.embera-embed-responsive-provider-flickr'].join(','));$(window).on('load resize',function(){$providers.each(function(){let oldHeight=$(this).find('iframe').attr('height'),oldWidth=$(this).find('iframe').attr('width');if(oldHeight&&oldWidth){let newWidth=$(this).width(),newHeight=(oldHeight / oldWidth)*newWidth;$(this).find('iframe').css({height:newHeight+'px',width:newWidth+'px'});}});});});